import React, { PureComponent } from 'react'
import Navigation from '../Navigation/Navigation'
import { MailOutlined, AppstoreOutlined, SettingOutlined } from '@ant-design/icons';
import { Menu } from 'antd';
import "./FrontPage.css"
export default class FrontPage extends PureComponent {
    componentDidMount(){
        const script = document.createElement("script");
        script.async = true;
        script.src = "../script.js";
        document.head.appendChild(script);
      }

    constructor(props) {
        super(props)
        this.state = {isActive:false,
                     current:'mail'}
      }
      handleClick = e => {
        console.log('click ', e);
        this.setState({ current: e.key });
        this.state.isActive=true;
      };
    render() {
        const { current } = this.state;
        let className = 'accordion';
        if (this.state.isActive) {
            className = 'accordion.active';
        }
        return (

            <div>
                <Navigation/>
                <script src="../script.js"></script>
                <div style={{width:"50vw", height:"100vh", float:"left",overflowY: "scroll"}}>
                    <div style={{marginLeft:"20px",float:"left"}}><h3>Building your Moneris Checkout Integration</h3></div>

                    <button class={className} onClick={() => this.handleClick()}>
                        <span class="number">&#9312;&nbsp;&nbsp;</span>Configuration
                    </button>
                    <button class="accordion"><span class="number">&#9313;&nbsp;&nbsp;</span>Client-Side Checkout Page</button>
                    <button class="accordion"><span class="number">&#9314;&nbsp;&nbsp;</span>Preload Request</button>
                    <button class="accordion"><span class="number">&#9315;&nbsp;&nbsp;</span>Displaying in the Browser</button>
                    <button class="accordion"><span class="number">&#9316;&nbsp;&nbsp;</span>Handling Callbacks</button>
                    <button class="accordion"><span class="number">&#9317;&nbsp;&nbsp;</span>Receipt Request</button>
                    <button class="accordion"><span class="number">&#9318;&nbsp;&nbsp;</span>Terminating Instance</button>
                    <button class="accordion"><span class="number">&#9319;&nbsp;&nbsp;</span>Testing MCO Integration</button>
                    <button class="accordion"><span class="number">&#9320;&nbsp;&nbsp;</span>Moving to Production with MCO</button>
                </div>
                <div style={{height:"100vh",width:"49vw",float:"left", backgroundColor:"yellow"}}>
                <Menu.Item key="mail" icon={<MailOutlined />}>
                     Navigation One
                </Menu.Item>
                <Menu.Item key="app" icon={<AppstoreOutlined />}>
                    Navigation Two
                </Menu.Item>
                </div>
                <script src="../script.js"></script>
            </div>
        )
    }
}
