import React, { PureComponent } from 'react'

let textValue = {
    "store_id":"store2",
    "api_token":"yesguy",
    "checkout_id":"chktD3MM4tore2",
    "txn_total":"2.00",
    "environment":"qa",
    "action":"preload"
};

export default class PreLoadRequest extends PureComponent {
    constructor(props) {
        super(props);

        this.textValue = {
            "store_id":"TBD",
            "api_token":"TBD",
            "checkout_id":"TBD",
            "txn_total":"2.00",
            "environment":"qa",
            "action":"preload"
        };


        this.state = { isToggleOn: true};

        // This binding is necessary to make `this` work in the callback
        this.handleClick = this.handleClick.bind(this);


    }

    handleClick() {
        this.setState(state => ({
            isToggleOn: !state.isToggleOn
        }));
    }



    preloadRequestFunction(code){
    var request = new XMLHttpRequest()
    openPage('preload', tabGreen, '#6DC7BE')
    request.open('POST', 'https://gatewayt.moneris.com/chkt/request/request.php')
    request.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
    request.onreadystatechange = function() {
      // Begin accessing JSON data here
      try {
        var data = JSON.parse(this.response)
        showObject(data);

        var codeJSON = JSON.stringify(this.response)
        codeJSON = codeJSON.replace(/"\\n|\\/g, '')
        codeJSON = codeJSON.replace(/}"/g, '}')

        var codeJSONFormatted = returnDataFormatted(codeJSON);
        var codeLinesPreload = '';
        if (request.status == 200 && request.readyState == 4) {
             var count = (codeJSONFormatted.match(/<br/g) || []).length
             var i;
             for (i = 1; i <= count+1; i++) {
               codeLinesPreload += "<span>" + i + "</span>";
             }
             codeLinesPreload = codeLinesPreload.replace(/\[object HTMLSpanElement\]/,'')
             codeLinesPreload = codeLinesPreload.replace(/\[object HTMLCollection\]/,'')
             document.getElementById("codeLinesPreload").innerHTML = codeLinesPreload
             document.getElementById("codePreload").innerHTML = codeJSONFormatted

           document.getElementById('loaderPreload').style.display = "none";
           document.getElementById('preloadSection').style.display = "inline";

           document.getElementById("ticketNumber").innerHTML = data.response.ticket
             document.getElementById("ticketNumber1").innerHTML = data.response.ticket
             document.getElementById("ticketNumber2").innerHTML = data.response.ticket
        }
      } catch (err) {
          if (request.readyState == 4) {
            document.getElementById("codeLinesPreload").innerHTML = ""
            document.getElementById("codePreload").innerHTML = "Unexpected end of JSON input";
            document.getElementById('loaderPreload').style.display = "none";
            document.getElementById('preloadSection').style.display = "inline";
            errorModal.style.display = "block";
          }
        }
      }

    request.send(code)
  }

    render() {
        this.textValue.store_id = this.props.StoreId;
        this.textValue.api_token = this.props.ApiToken;
        this.textValue.checkout_id = this.props.CheckOutId;
        console.log(this.textValue) 
        if (this.state.isToggleOn) {
            return (
                <div>
                    <button class="accordion" onClick={this.handleClick}><span class="number">&#9314;&nbsp;&nbsp;</span>PreLoadRequest</button>
                </div>
            )
        } else {
            return (
                <div>
                    <div>
                        <button class="accordion" onClick={this.handleClick}><span class="number">&#9314;&nbsp;&nbsp;</span>PreLoadRequest</button>
                    </div>
                    <div class="panel">
                        <p>
                            The Preload request is the means by which a Moneris Checkout instance is securely generated at transaction time. It involves a server-to-server post using the JSON format.<br></br><br></br>
                            The response to the Preload request returns a ticket number which uniquely identifies the instance and must be passed in the JavaScript <b>monerisCheckout.startCheckout(ticket #)</b> request in order to display the Moneris Checkout page in the browser.<br></br><br></br>
                            In your server implementation, use the following Moneris Checkout URLs to post to, depending on the development stage:<br></br>
                            Testing: <b>https://gatewayt.moneris.com/chkt/request/request.php</b><br></br><br></br>
                            Production: <b>https://gateway.moneris.com/chkt/request/request.php</b><br></br><br></br>
                            <textarea id="preloadRequestCode" value={JSON.stringify(this.textValue,null,2) } rows="9" cols="50" spellcheck="false">
                            </textarea>
                            <button class="btn" onclick="preloadRequestFunction(document.getElementById('preloadRequestCode').value)">Make Preload Request</button>
                            <br></br><br></br>
                            <div id="morePreload">
                                <h3>Request fields for Preload request &ndash; Required</h3>
                                <div id="tablePreloadRequest" style={{ fontSize: "12px" }}>#</div>
                                <br></br><br></br>
                            </div>
                            <button class="more" id="btnMoreLessPreload" onclick="readMoreLess('morePreload', 'btnMoreLessPreload')">Read more</button>
                        </p>
                    </div>
                </div>
            )
        }
    }
}
