import React, { PureComponent } from 'react'
import Navigation from '../Navigation/Navigation'
import "./FrontPage.css"
export default class FrontPage extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {'active': false, 'class': 'album'};
      }
    handleClick() {
        console.log('Click happened');
    }
    render() {
        let className = 'menu';
        if (this.props.isActive) {
        className += ' menu-active';
        }
        return (

            <div>
                <Navigation/>
                <div style={{width:"50vw", height:"100vh", float:"left",overflowY: "scroll"}}>
                    <div style={{marginLeft:"20px",float:"left"}}><h3>Building your Moneris Checkout Integration</h3></div>

                    <button class="accordion" onClick={() => this.handleClick()}>
                        <span class="number">&#9312;&nbsp;&nbsp;</span>Configuration
                        </button>

                     <div class="panel">
                         <p>The first step is to configure your Moneris Checkout page in the Moneris Merchant Resource Center (MRC).</p>
                     </div>



                </div>
                <div style={{height:"100vh",width:"49vw",float:"left", backgroundColor:"yellow"}}/>
            </div>
        )
    }
}
